import React from 'react';

const myList = ['Головна', 'Про мене', 'Навички', 'Портфоліо'];

const Header = () => {
	const [activ, setActiv] = React.useState('Головна');
	return (
		<div className="container">
			<div className={header_body}>
				<div className={header_logotyp}>
					Web Deweloper <span>Kondratiev</span>
				</div>
				<ul className={header_myList}>
					{myList.map((obj, index) => (
						<li
							className={activ === obj ? '.activ' : ' '}
							onClick={() => {
								setActiv(obj);
							}}
							key={index}>
							{obj}
						</li>
					))}
				</ul>
			</div>
		</div>
	);
};

export default Header;
