import styil from './header.module.scss';
import React from 'react';

const myList = ['Головна', 'Про мене', 'Навички', 'Портфоліо'];

const Header = () => {
	const [activ, setActiv] = React.useState('Головна');
	return (
		<header className={styil.root}>
			<div className="container">
				<div className={styil.body}>
					<div className={styil.logotyp}>
						Web Deweloper <span>Kondratiev</span>
					</div>
					<ul className={styil.myList}>
						{myList.map((obj, index) => (
							<li className={activ === obj ? 'activ' : ' '} key={index}>
								{obj}
							</li>
						))}
					</ul>
				</div>
			</div>
		</header>
	);
};

export default Header;
