import React from 'react';
import styil from './about.module.scss';
const About = ({ statePage }) => {
	const [buttonActiv, setButtonActiv] = React.useState(false);
	console.log(buttonActiv);
	return (
		<div className={statePage === 'Про мене' ? 'about__root _block activPage' : 'about__root _block'}>
			<div className="container">
				<div className="about__body">
					<div className="about__content">
						<h1>Привіт, я Артем Кондратьєв.</h1>
					</div>
					<div className={buttonActiv ? styil.button.activ : styil.button} onClick={() => setButtonActiv(!true)}>
						<p>кнопка</p>
					</div>
				</div>
			</div>
		</div>
	);
};

export default About;
