import React from 'react';

const myList = [{ page: 'Головна', class: 'header__activ icon-home_icon' }, 'Про мене', 'Навички', 'Портфоліо'];

const Header = ({ valueState }) => {
	const [activ, setActiv] = React.useState('Головна');
	const setValueState = (obj) => {
		setActiv(obj);
		valueState(obj);
	};
	return (
		<div className="container">
			<div className="header__body">
				<div className="header__logotyp">
					Web Deweloper <span>Kondratiev</span>
				</div>
				<ul className="header__myList">
					{myList.map((obj, index) => (
						<li
							className={activ === obj ? 'header__activ' : ' '}
							onClick={() => {
								setValueState(obj);
							}}
							key={index}>
							{obj}
						</li>
					))}
				</ul>
			</div>
		</div>
	);
};

export default Header;
