import html from '../img/icons/html.svg';
import css from '../img/icons/css.svg';
import js from '../img/icons/js.svg';
import react from '../img/icons/react.svg';
import redux from '../img/icons/redux.svg';
import ts from '../img/icons/ts.svg';
import git from '../img/icons/git.svg';
import vsc from '../img/icons/vsc.svg';
import figma from '../img/icons/figma.svg';

export const education = [
	{
		icon: html,
		title: 'HTML',
		text: 'При вивчені HTML, дізнався що це не язик програмування, а просто розмітка гіпер силок. Також що є різні типи тегів, а саме строкові та блокові. Знайьомство з сімейством input та силками, і розновидностями типів. Звісно з симатикою та навіщо воно все це потрібно. Canvas потрібно ще підтягнути, але вже розумію що за допомогою цієї технології можна робить круті анімації.',
	},
	{
		icon: css,
		title: 'CSS/SCSS',
		text: 'Крок до чарівного. Так можна назвати технологію css. Та йього препроцесор sass та lass. Препроцесори значно зспростовує роботу з цією технологією. Сам я використовую scss/sass що є майже тим самим за винятком деяких деталей. Також знайомий з: animacion keyfrom, transform, posicion, media запитами. Та ще багато чого цікавого.',
	},
	{
		icon: js,
		title: 'JS',
		text: 'Js неможливо описати декількома словами, це насправді нервова система додатків. Працює з DOM елементами та є справжньою мовою програмування. Мало знати базові речі цієї технології, потрібні справжні навички та розуміння алгоритмів. Взагалі вивчав самостійно всі технології, але для саморозуміння пройшов курс Лущенка Олександра js 2.0 де отримав сертифікат та підтвердження того що володію базовими навичками. Перевірить сертифікат можливо за посиланням https://itgid.info/ru/certificate та номер сертифікату: 99ucj1uwrhd4. В цьому курсі є дкілька міні проєктів повязані з ajax запитами, події мишки та клавіатури. Я їх додам до свого потфоліо',
	},
	{
		icon: react,
		title: 'REACT',
		text: 'React як на мене це логічне продовження навчання та підвищення рівння навичок, є розуміння що це обєднання html та js технологій. Доречі цей сайт я написав за допомогою react технології',
	},
	{
		icon: redux,
		title: 'REDUX/Toolcit',
		text: 'Стейт менеджер який відповів мені на деякі мої питання, але ще потрібно його дослідить.',
	},
	{
		icon: ts,
		title: 'ts',
		text: 'Я вичав базові теги та симатику',
	},
	{
		icon: ts,
		title: 'NPM',
		text: 'Я вичав базові теги та симатику',
	},
	{
		icon: git,
		title: 'git/git-page',
		text: 'Я вичав базові теги та симатику',
	},
	{
		icon: vsc,
		title: 'VSCode',
		text: 'Я вичав базові теги та симатику',
	},
	{
		icon: figma,
		title: 'figma',
		text: 'Я вичав базові теги та симатику',
	},
];

export const experience = [
	{
		title: 'Комерційний досвід',
		text: 'Нажаль в мене ще не має комерційного досвіду, але є практичний, який ти можеш переглянути в моєму портфоліо. Ти завжди можеш допомогти мені з роботою, зроби замовленя або запропонуй мені роботу.',
	},
];
